import { Component } from 'react'

import './app.sass'

import AppInfo from '../app-info/app-info'
import SearchPanel from '../search-panel/search-panel'
import AppFilter from '../app-filter/app-filter'
import EmployeesList from '../employees-list/employees-list';
import EmployeesAddForm from '../employees-add-form/employees-add-form'

class App extends Component {
    constructor(props) {
        super(props)
        this.state = {
            data: [
                { name: 'Olha S.', salary: 3200, increase: false, id: 1 },
                { name: 'Alex M.', salary: 2200, increase: true , id: 2},
                { name: 'Mark A.', salary: 1200, increase: false, id:  3},
                ]
        }
    }

    deleteItem = (id) => {
        this.setState(({ data }) => {
            const index = data.findIndex(el => el.id === id)

            // const newData = data.slice(0, index)
            // const oldData = data.slice(index + 1)
            // const newArr = [...newData, ...oldData]

            return {
                data: data.filter
            }
        })
    }

    render() {
        return (
      <div className='app'>
        <AppInfo />
        <div className='search-panel'>
          <SearchPanel />
          <AppFilter />
        </div>
            <EmployeesList
                data={this.state.data}
                onDelete={this.deleteItem}
            />
            <EmployeesAddForm />
      </div>
    );
    }

}

export default App
