  }



  render() {
      return (
        <div className='app-add-form'>
          <h3>Add new employee</h3>
          <form className='add-form d-flex'>
            <input
              type='text'
              className='form-control new-post-label'
              placeholder='Name'
              o
            />
            <input
              type='number'
              className='form-control new-post-label'
              placeholder='Salary'
              o
            />

            <button type='submit' className='btn btn-outline-light'>
              Add
            </button>
          </form>
        </div>
      );
    }
}

export default EmployeesAddForm;
